import{_ as t,p as e,q as d,a1 as i}from"./framework-204010b2.js";const l={},a=i('<h1 id="ministudio-快捷参考" tabindex="-1"><a class="header-anchor" href="#ministudio-快捷参考" aria-hidden="true">#</a> MiniStudio 快捷参考</h1><hr><p>本指南概述了 MiniStudio 的基本功能，这是用于在 MiniStudio 中制作游戏、编写游戏脚本和测试游戏的多合一工具。</p><h2 id="转换对象" tabindex="-1"><a class="header-anchor" href="#转换对象" aria-hidden="true">#</a> 转换对象</h2><hr><p>你可以通过<code>Home</code>（主页）选项卡访问<code>Studio</code>转换工具，从而定位对象和调整对象大小。</p><table><thead><tr><th style="text-align:left;"><div style="width:150px;"><strong>快捷键</strong></div></th><th style="text-align:left;"><div style="width:150px;"><strong>动作</strong></div></th><th style="text-align:left;"><div style="width:500px;"><strong>描述</strong></div></th></tr></thead><tbody><tr><td style="text-align:left;"><code>Shift + F1</code></td><td style="text-align:left;">选择</td><td style="text-align:left;">可用于在工作区选择对象</td></tr><tr><td style="text-align:left;"><code>Shift + F2</code></td><td style="text-align:left;">移动</td><td style="text-align:left;">显示三个手柄，可拖动手柄，以沿着相应的 X 轴、Y 轴或 Z 轴移动当前选项</td></tr><tr><td style="text-align:left;"><code>Shift + F3</code></td><td style="text-align:left;">缩放</td><td style="text-align:left;">显示三个手柄，可拖动该手柄，以沿着相应的 X 轴、Y 轴或 Z 轴调整当前选项的大小。请注意，模型 只能整体缩放</td></tr><tr><td style="text-align:left;"><code>Shift + F4</code></td><td style="text-align:left;">旋转</td><td style="text-align:left;">显示三个手柄，可拖动该手柄，以围绕相应的 X 轴、Y 轴或 Z 轴旋转当前选项</td></tr></tbody></table>',7),o=[a];function n(s,r){return e(),d("div",null,o)}const c=t(l,[["render",n],["__file","HomeOperation.html.vue"]]);export{c as default};
